import { addDoc, collection, getFirestore } from "firebase/firestore";
import React from "react";
import { Link } from "react-router-dom";
import { useCartContext } from "../../context/CartContext";
import ItemCart from "../ItemCart";
import "./Cart.css";

const Cart = () => {
	const { cart, totalPrice } = useCartContext();
	const [active, setActive] = React.useState(false)
	const order = {
		buyer: {
			name: "Pedro",
			email: "pedroverde.cl@gmail.com",
			phone: "11290219021",
			address: "Santiago",
		},
		items: cart.map((product) => ({
			id: product.id,
			title: product.title,
			price: product.price,
			quantity: product.quantity,
		})),
		total: totalPrice(),
	};

	const handleSubmit = (e) => {
		e.preventDefault();
		setActive(true)

	}

	const handleClick = () => {
		const db = getFirestore();
		const ordersCollection = collection(db, "orders");
		addDoc(ordersCollection, order).then(({ id }) => alert("Reserva realizada pronto de contactaremos", id));
	};

	if (cart.length === 0) {
		return (
			<>
				<div>
				<Link to="/" className="compra-texto">Hacer compras </Link>
				<p className="texto-carrito">No hay elementos en el carrito</p>
				<img src="https://peverdemarketing.com/wp-content/uploads/2022/12/Townhouse-250-×-250-px-1080-×-1080-px.png" alt="logo"></img>
				<div></div>

				</div>
			</>
		);
	}

	return (
		<>
			{cart.map((product) => (
				<ItemCart key={product.id} product={product} />
			))}
			<p>total: {totalPrice()}</p>

		<form onSubmit={handleSubmit}>
			<input className="formulario" type="text" placeholder="Ingresa tu nombre y apellido"/> 
			<input className="formulario" type="text" placeholder="Ingresa tu correo" required/> 
			<button type="submit"> Enviar Datos</button>
		</form>
	{


		active && (
			<div className="realizar-compra-div">
			<Link to="/gracias" className="realizar-compra" onClick={handleClick}>Realizar Compra</Link>
			</div>
		)
	}

		</>
	);
};

export default Cart;
