import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import ItemDetailContainer from "../ItemDetailContainer";
import axios from 'axios';

function HomeScreen() { 
    const [inmuebles, setInmuebles] = useState([]);
    useEffect(()=>{
      const fetchData =async () => {
        const result = await axios.get('/api/inmuebles');
        setInmuebles(result.ItemDetailContainer);
      }
    }, []);
    return (
    <div>
    <h1>Lista de Inmuebles</h1>
        <div className="inmuebles">
        {
          ItemDetailContainer.inmuebles.map(inmuebles=>(
            <div className="inmueble" key={inmuebles.slug}>
              <Link to={`/inmuebles/${inmuebles.slug}`}>
              <img src={inmuebles.image} alt={inmuebles.name}/>
            </Link>
            <div className="inmueble-info">
              <Link to={`/inmueble/${inmuebles.slug}`}>
                <p>{inmuebles.nombre}</p>
              </Link>
              <p>{inmuebles.precio}</p>
              <button>AÃ±adir al carrito</button>
            </div>
          </div>
          ))}
        </div>
    </div>
    );
}


export default HomeScreen;
